
public class StringFunc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Nguyen Thi Hong Hanh";
				System.out.println("Tên bạn có " + name.length() + " ki tự");
				System.out.println("Tên bạn bằng chữ in hoa là " + name.toUpperCase());
				System.out.println("Tên bạn bằng chữ in thường là " + name.toLowerCase());
	}

}
